# Write your MySQL query statement below
with a as (select student_id, student_name, subject_name from Students cross join Subjects)
select a.student_id, a.student_name, a.subject_name, count(e.subject_name) as attended_exams from a left join Examinations e on a.student_id=e.student_id and a.subject_name=e.subject_name group by a.student_id,a.subject_name, a.student_name order by a.student_id, a.subject_name